events.inlineCode:
const player = objects[0].getBehavior("PlatformerObject");

if (runtimeScene.getTimeManager().isFirstFrame())   //initialization
{
    objects[0].doubleJump = false;
    objects[0].bulletCooldown = 0;
    objects[0].invTime = 0;
    objects[0].spawnX = objects[0].getX();
    objects[0].spawnY = objects[0].getY();
}

//don't let the player fall forever
console.log(objects[0].getY());
if(objects[0].getY() > 3000)
{
    objects[0].getVariables().get("TakeDamage").setNumber(1);
    objects[0].setX(objects[0].spawnX);
    objects[0].setY(objects[0].spawnY);
}

//input

if (player.isOnFloor())
{
    if(player.isMoving() > 0)
    {
        objects[0].setAnimation(1);
    }
    else
    {
        objects[0].setAnimation(0);
    }
}
else objects[0].setAnimation(2);

const UPKEY = 38;
const SPACEKEY = 32;
const LEFTKEY = 37;
const RIGHTKEY = 39;
const ZKEY = 90;

if (runtimeScene.getGame().getInputManager().isKeyPressed(LEFTKEY)) 
{
  objects[0].flipX(true);
} 
else if (runtimeScene.getGame().getInputManager().isKeyPressed(RIGHTKEY)) 
{
  objects[0].flipX(false);
}
if (runtimeScene.getGame().getInputManager().isKeyPressed(SPACEKEY) || runtimeScene.getGame().getInputManager().isKeyPressed(UPKEY)) 
{
    if(!player.isOnFloor()) objects[0].doubleJump = true;

    if(!objects[0].doubleJump)
        player.simulateControl("Jump");
}
if(player.isOnFloor())
{
    objects[0].doubleJump = false;
}

//shooting

if (runtimeScene.getGame().getInputManager().isKeyPressed(ZKEY)) 
{
    debugger;
    if (objects[0].bulletCooldown == 0)
    {
        var b = runtimeScene.createObject("Bullet");
        b.setPosition(objects[0].getX(), objects[0].getY() + 20);
        //debugger;
        b.flipX(objects[0]._flippedX);
        b.lifetime = 0;
        var mx = runtimeScene.getGame().getInputManager().getMouseX();
        var my = runtimeScene.getGame().getInputManager().getMouseY();
        b.addForce(600 * (b._flippedX ? -1 : 1), 0, 1);

        objects[0].bulletCooldown = 20;
    }
}
if(objects[0].bulletCooldown > 0) objects[0].bulletCooldown -= 1;

//camera follow

runtimeScene.getLayer("Base Layer").setCameraX(objects[0].getX());
runtimeScene.getLayer("Base Layer").setCameraY(objects[0].getY());

//health UI

var pHealth = objects[0].getVariables().get("Health").getAsNumber();

var hstr = "";
for (var i = 0; i < pHealth; i++)
    hstr += 'â™¥';

runtimeScene.getObjects("HealthText")[0].setString(hstr);

//take damage

if(objects[0].getVariables().get("TakeDamage").getAsNumber() > 0)
{
    if(objects[0].invTime <= 0)
    {
        objects[0].getVariables().get("Health").setNumber(pHealth - 1);
        objects[0].invTime = 1000;
    }
    objects[0].getVariables().get("TakeDamage").setNumber(0);
}

//invincibility

if(objects[0].invTime > 0)
{
    objects[0].invTime -= objects[0].getElapsedTime(runtimeScene);

    if(Math.sin(objects[0].invTime / 10) < 0)
        objects[0].setOpacity(50);
    else
        objects[0].setOpacity(255);
}
else
{
    objects[0].setOpacity(255);
}

objects.forEach( object =>
{
    if(object.lifetime > 500)
    {
        object.deleteFromScene(runtimeScene);
        var bp = runtimeScene.createObject("BulletParticle");
        bp.setPosition(object.getX() + object.getCenterX(), object.getY() + object.getCenterY());
    }
    else
    {
        //object.setPosition(object.getX() + (object._flippedX ? -10 : 10), object.getY());
        object.lifetime += object.getElapsedTime(runtimeScene);
    }
}
)
